# encoding=utf-8""""""import matplotlibfrom AutoDailyOpt.Debug_Sub import debug_print_txtfrom DataSource.Code2Name import code2name# matplotlib.use('agg')import calendarimport pandas as pdimport talibimport wxfrom PIL import Imagefrom io import BytesIOfrom pylab import *from Config.AutoStkConfig import plot_current_days_amountfrom DataSource.Data_Sub import get_k_data_JQ, my_pro_barfrom Experiment.CornerDetectAndAutoEmail.Sub import add_stk_index_to_dffrom Experiment.MACD_Stray_Analysis.Demo1 import plot_W_Mfrom SDK.MyTimeOPT import add_date_str, get_current_date_str, get_current_datetime_strfrom SDK.PlotOptSub import addXticklabel_list, addXticklabeldef convert_pic_to_data(pic_url):    img = Image.open(pic_url)    output = BytesIO()  						# 如是StringIO分引起TypeError: string argument expected, got 'bytes'    img.convert("RGB").save(output, "PNG")  	# 以BMP格式保存流    img.close()    wx_img = wx.Image(output, wx.BITMAP_TYPE_PNG)    output.close()    return wx_imgdef convert_fig_to_img(fig):    output = BytesIO()  # BytesIO实现了在内存中读写byte    fig.canvas.print_png(output, dpi=20)    output.seek(0)    img = wx.Image(output, wx.BITMAP_TYPE_ANY)    output.close()    plt.close(fig)    return imgdef gen_hour_macd_values(stk_code, source='jq', title=''):    if source == 'jq':        df_30 = get_k_data_JQ(stk_code, count=120,                              end_date=add_date_str(get_current_date_str(), 1), freq='30m')        df_60 = get_k_data_JQ(stk_code, count=120,                              end_date=add_date_str(get_current_date_str(), 1), freq='60m')    elif source == 'ts':        df_30 = my_pro_bar(stk_code, start=add_date_str(get_current_date_str(), -20), freq='30min')        df_60 = my_pro_bar(stk_code, start=add_date_str(get_current_date_str(), -20), freq='60min')    # 去掉volume为空的行    df_30 = df_30.loc[df_30.apply(lambda x: not (x['volume'] == 0), axis=1), :]    df_60 = df_60.loc[df_60.apply(lambda x: not (x['volume'] == 0), axis=1), :]    df_30['MACD'], _, _ = talib.MACD(df_30.close,                                     fastperiod=12, slowperiod=26,                                     signalperiod=9)    df_60['MACD'], _, _ = talib.MACD(df_60.close,                                     fastperiod=12, slowperiod=26,                                     signalperiod=9)    # 生成图片    df_30 = df_30.dropna()    df_60 = df_60.dropna()    if str(df_60.index[-1]) > get_current_datetime_str():        df_30 = df_30[:-1]        df_60 = df_60[:-1]    return df_30, df_60def gen_hour_macd_pic(stk_data, source='jq', title='', debug=False, stk_code=''):    if debug:        debug_print_txt('macd_hour_pic', stk_code, get_current_datetime_str() + ':\n----------------------\n\n')    # 生成小时macd数据    # df_30, df_60 = gen_hour_macd_values(stk_code, source=source, title=title)    df_30, df_60 = stk_data        if debug:        debug_print_txt('macd_hour_pic', stk_code, 'df_30原始数据:\n' + str(df_30) + '\n\n' + 'df_60原始数据:\n' + str(df_60) + '\n\n')    # 根据情况设置背景色    attention = False    m30 = df_30.tail(3)['MACD'].values    m60 = df_60.tail(3)['MACD'].values    if debug:        debug_print_txt('macd_hour_pic', stk_code, 'm30原始数据:\n' + str(m30) + '\n\n' + 'm60原始数据:\n' + str(m60) + '\n\n')    if (m30[1] == np.min(m30)) | (m60[1] == np.min(m60)):        # 设置背景红        set_background_color('b_r')    elif (m30[1] == np.max(m30)) | (m60[1] == np.max(m60)):        # 设置背景绿        set_background_color('b_g')    else:        set_background_color()    fig, ax = plt.subplots(ncols=1, nrows=4)    ax[0].plot(range(0, len(df_30)), df_30['close'], 'g*--', label='close_30min')    ax[1].bar(range(0, len(df_30)), df_30['MACD'], label='MACD_30min')    ax[2].plot(range(0, len(df_60)), df_60['close'], 'g*--', label='close_60min')    ax[3].bar(range(0, len(df_60)), df_60['MACD'], label='MACD_60min')    # 设置下标    ax[1] = addXticklabel_list(        ax[1],        list([str(x)[-11:-3] for x in df_30['datetime']]),        15, rotation=0, fontsize=6)    ax[3] = addXticklabel_list(        ax[3],        list([str(x)[-11:-3] for x in df_60['datetime']]),        15, rotation=0, fontsize=6)    for ax_sig in ax:        ax_sig.legend(loc='best')    # 设置标题    if m30[1] == np.min(m30):        title = stk_code + '半小时MACD低点！'        # ax[0].set_title(stk_code + '半小时MACD低点！')    elif m60[1] == np.min(m60):        title = stk_code + '小时MACD低点！'        # ax[0].set_title(stk_code + '小时MACD低点！')    elif m30[1] == np.max(m30):        title = stk_code + '半小时MACD高点！'        # ax[0].set_title(stk_code + '半小时MACD高点！')    elif m60[1] == np.max(m60):        title = stk_code + '小时MACD高点！'        # ax[0].set_title(stk_code + '小时MACD高点！')    else:        title = stk_code    ax[0].set_title(title)    if debug:        debug_print_txt('macd_hour_pic', stk_code, '结论:' + title + '\n\n')    fig.tight_layout()    plt.subplots_adjust(wspace=0, hspace=0.3)  # 调整子图间距    return figdef gen_hour_index_pic_wx(stk_data, source='jq', title='', debug=False):    # 下载数据    # if source == 'jq':    #     df_30 = get_k_data_JQ(stk_data, count=120,    #                           end_date=add_date_str(get_current_date_str(), 1), freq='30m')    #    # else:    #     df_30 = my_pro_bar(stk_data, start=add_date_str(get_current_date_str(), -20), freq='30min')    # 清楚时间未到的数据    df_30 = stk_data.loc[stk_data.apply(lambda x: not (x['volume'] == 0), axis=1), :]    fig_tmp = gen_hour_idx_pic(df_30, stk_code='', debug=True)[0]    img = convert_fig_to_img(fig_tmp)    return imgdef gen_hour_macd_pic_local(stk_data, source='jq', title='', save_dir=''):    fig_tmp = gen_hour_macd_pic(stk_data, source=source, title=title, debug=True)    plt.savefig(save_dir)    fig_tmp.close()def gen_hour_macd_pic_wx(stk_data, source='jq', title=''):    """    :param stk_data:    :param source:    :param title:    :return:    """    fig_tmp = gen_hour_macd_pic(stk_data, source=source, title=title, debug=True)    img = convert_fig_to_img(fig_tmp)    return imgdef gen_w_m_macd_pic(stk_data, stk_code=''):    """    :param stk_data:    :param towho:    :return:    """    # 获取今天的情况，涨幅没有超过3%的不考虑    # df_now = get_k_data_JQ(stk_code, count=2, end_date=get_current_date_str()).reset_index()    #    # if (df_now.tail(1)['close'].values[0]-df_now.head(1)['close'].values[0])/df_now.head(1)['close'].values[0] < 0.03:    #     print('函数week_MACD_stray_judge：' + stk_code + '涨幅不够！')    #     return False    # df = get_k_data_JQ(stk_code, count=400, end_date=get_current_date_str()).reset_index()    df = stk_data    if len(df) < 350:        print('函数week_MACD_stray_judge：' + stk_data + '数据不足！')        return False    # 规整    df_floor_origin = df.tail(math.floor(len(df) / 20) * 20 - 19)    df_floor = df_floor_origin.copy()    # 增加每周的星期几    df_floor['day'] = df_floor_origin.apply(        lambda x: calendar.weekday(int(x['date'].split('-')[0]), int(x['date'].split('-')[1]),                                   int(x['date'].split('-')[2])), axis=1)    # # 增加每周的星期几    # df_floor['day'] = df_floor_origin.apply(    #     lambda x: calendar.weekday(int(x['date'].split('-')[0]), int(x['date'].split('-')[1]),    #                                int(x['date'].split('-')[2])), axis=1)    # 隔着5个取一个    if df_floor.tail(1)['day'].values[0] != 4:        df_floor_slice_5_origin = pd.concat([df_floor[df_floor.day == 4], df_floor.tail(1)], axis=0)    else:        df_floor_slice_5_origin = df_floor[df_floor.day == 4]    # 获取最后的日期    date_last = df_floor_slice_5_origin.tail(1)['date'].values[0]    df_floor_slice_5 = df_floor_slice_5_origin.copy()    # 计算指标    df_floor_slice_5['MACD'], df_floor_slice_5['MACDsignal'], df_floor_slice_5['MACDhist'] = talib.MACD(        df_floor_slice_5.close,        fastperiod=6, slowperiod=12,        signalperiod=9)    # 隔着20个取一个（月线）    df_floor_slice_20_origin = df_floor.loc[::20, :]    df_floor_slice_20 = df_floor_slice_20_origin.copy()    # 计算指标    df_floor_slice_20['MACD'], df_floor_slice_20['MACDsignal'], df_floor_slice_20['MACDhist'] = talib.MACD(        df_floor_slice_20.close,        fastperiod=4,        slowperiod=8,        signalperiod=9)    set_background_color(bc='w')    """ --------------------------------------- 生成图片 -------------------------------------"""    fig, ax = plot_W_M(df_floor_slice_5, df_floor_slice_20)    # 增加标题    plt.title(stk_code + 'month-stray' + date_last)    return figdef gen_w_m_macd_pic_wx(stk_code):    fig_tmp = gen_w_m_macd_pic(stk_code)    img = convert_fig_to_img(fig_tmp)    return imgdef gen_day_pic(stk_df, stk_code=''):    """    函数功能：给定stk的df，已经确定stk当前处于拐点状态，需要将当前stk的信息打印成图片，便于人工判断！    :param stk_df           从tushare下载下来的原生df    :param root_save_dir    配置文件中定义的存储路径    :return:                返回生成图片的路径    """    """    规划一下都画哪些图    1、该stk整体走势，包括60日均线、20日均线和收盘价    2、stk近几天的MACD走势    """    """    在原数据的基础上增加均线和MACD    """    # 按升序排序    stk_df = stk_df.sort_values(by='date', ascending=True)    stk_df['M20'] = stk_df['close'].rolling(window=20).mean()    stk_df['M60'] = stk_df['close'].rolling(window=60).mean()    stk_df['MACD'], stk_df['MACDsignal'], stk_df['MACDhist'] = talib.MACD(stk_df.close,                                                                          fastperiod=12, slowperiod=26,                                                                          signalperiod=9)    # 检查日级别的MACD是否有异常    attention = False    MACD_list = stk_df.tail(3)['MACD'].values    if MACD_list[1] == np.min(MACD_list):        attention = True        # 设置背景红        set_background_color('b_r')    elif MACD_list[1] == np.max(MACD_list):        attention = True        # 设置背景绿        set_background_color('b_g')    else:        set_background_color()    fig, ax = plt.subplots(nrows=4, ncols=1)    ax[0].plot(range(0, len(stk_df['date'])), stk_df['M20'], 'b--', label='20日均线', linewidth=1)    ax[0].plot(range(0, len(stk_df['date'])), stk_df['M60'], 'r--', label='60日均线', linewidth=1)    ax[0].plot(range(0, len(stk_df['date'])), stk_df['close'], 'g*--', label='收盘价', linewidth=0.5, markersize=1)    ax[1].bar(range(0, len(stk_df['date'])), stk_df['MACD'], label='MACD')    # 准备下标    xticklabels_all_list = list(stk_df['date'].sort_values(ascending=True))    xticklabels_all_list = [x.replace('-', '')[2:] for x in xticklabels_all_list]    for ax_sig in ax[0:2]:        ax_sig = addXticklabel_list(ax_sig, xticklabels_all_list, 30, rotation=45)        ax_sig.legend(loc='best', fontsize=5)    # 画出最近几天的情况（均线以及MACD）    stk_df_current = stk_df.tail(plot_current_days_amount)    ax[2].plot(range(0, len(stk_df_current['date'])), stk_df_current['M20'], 'b--', label='20日均线', linewidth=2)    ax[2].plot(range(0, len(stk_df_current['date'])), stk_df_current['M60'], 'r--', label='60日均线', linewidth=2)    ax[2].plot(range(0, len(stk_df_current['date'])), stk_df_current['close'], 'g*-', label='收盘价', linewidth=1,               markersize=5)    ax[3].bar(range(0, len(stk_df_current['date'])), stk_df_current['MACD'], label='MACD')    # 设置标题并返回分析结果    result_analysis = []    if MACD_list[1] == np.min(MACD_list):        title_tmp = stk_code + ' ' + code2name(stk_code) + ' 日级别 MACD 低点！后续数天可能上涨！'        ax[0].set_title(title_tmp)        result_analysis.append(title_tmp)    elif MACD_list[1] == np.max(MACD_list):        title_tmp = stk_code + ' ' + code2name(stk_code) + ' 日级别 MACD 高点！后续数天可能下跌！'        ax[0].set_title(title)        result_analysis.append(title_tmp)    # 准备下标    xticklabels_all_list = list(stk_df_current['date'].sort_values(ascending=True))    xticklabels_all_list = [x.replace('-', '')[2:] for x in xticklabels_all_list]    for ax_sig in ax[2:4]:        ax_sig = addXticklabel_list(ax_sig, xticklabels_all_list, 30, rotation=45)        ax_sig.legend(loc='best', fontsize=5)    fig.tight_layout()                          # 调整整体空白    plt.subplots_adjust(wspace=0, hspace=1)     # 调整子图间距    # plt.close()    return fig, ax, attention, result_analysisdef gen_day_pic_wx(stk_df, stk_code=''):    r_tuple = gen_day_pic(stk_df, stk_code=stk_code)    fig_tmp = r_tuple[0]    img = convert_fig_to_img(fig_tmp)    return img, r_tuple[3]def gen_idx_pic(stk_df, stk_code=''):    """    打印常用指标    """    # 按升序排序    stk_df = stk_df.sort_values(by='date', ascending=True)    """    增加指标    'RSI5', 'RSI12', 'RSI30'    'SAR'    'slowk', 'slowd'    'upper', 'middle', 'lower'    'MOM'    """    stk_df = add_stk_index_to_df(stk_df).tail(60)    set_background_color(bc='w')    result_analysis = []    # 检查SAR    attention = False    sar_tail_origin = stk_df.tail(2)    sar_tail = sar_tail_origin.copy()    sar_tail['compare'] = sar_tail_origin.apply(lambda x: x['SAR'] - x['close'], axis=1)    title_tmp = stk_code + ' ' + code2name(stk_code)    if sar_tail.head(1)['compare'].values[0] * sar_tail.tail(1)['compare'].values[0] < 0:        if sar_tail.tail(1)['SAR'].values[0] < sar_tail.tail(1)['close'].values[0]:            title_tmp = stk_code + ' ' + code2name(stk_code) + ' 注意 SAR 指标翻转，后续数天可能上涨！'            result_analysis.append(title_tmp)            set_background_color(bc='b_r')        else:            title_tmp = stk_code + ' ' + code2name(stk_code) + ' 注意 SAR 指标翻转，后续数天可能下跌！'            result_analysis.append(title_tmp)            set_background_color(bc='b_g')        attention = True    fig, ax = plt.subplots(nrows=5, ncols=1)    ax[0].plot(range(0, len(stk_df['date'])), stk_df['RSI5'], 'b--', label='RSI5线', linewidth=1)    ax[0].plot(range(0, len(stk_df['date'])), stk_df['RSI12'], 'r--', label='RSI12线', linewidth=1)    ax[0].plot(range(0, len(stk_df['date'])), stk_df['RSI30'], 'g*--', label='RSI30', linewidth=0.5, markersize=1)    ax[0].plot(range(0, len(stk_df['date'])), [20 for a in range(len(stk_df['date']))], 'b--', linewidth=0.3)    ax[0].plot(range(0, len(stk_df['date'])), [80 for a in range(len(stk_df['date']))], 'b--', linewidth=0.3)    ax[0].set_ylim(0, 100)    ax[1].plot(range(0, len(stk_df['date'])), stk_df['SAR'], 'r--', label='SAR', linewidth=0.5, markersize=1)    ax[1].plot(range(0, len(stk_df['date'])), stk_df['close'], 'g*--', label='close', linewidth=0.5, markersize=1)    ax[2].plot(range(0, len(stk_df['date'])), stk_df['slowk'], 'g*--', label='slowk', linewidth=0.5, markersize=1)    ax[2].plot(range(0, len(stk_df['date'])), stk_df['slowd'], 'r*--', label='slowd', linewidth=0.5, markersize=1)    ax[2].plot(range(0, len(stk_df['date'])), [20 for a in range(len(stk_df['date']))], 'b--', linewidth=0.3)    ax[2].plot(range(0, len(stk_df['date'])), [80 for a in range(len(stk_df['date']))], 'b--', linewidth=0.3)    ax[2].set_ylim(0, 100)    ax[3].plot(range(0, len(stk_df['date'])), stk_df['upper'], 'r*--', label='布林上线', linewidth=0.5, markersize=1)    ax[3].plot(range(0, len(stk_df['date'])), stk_df['middle'], 'b*--', label='布林均线', linewidth=0.5, markersize=1)    ax[3].plot(range(0, len(stk_df['date'])), stk_df['lower'], 'g*--', label='布林下线', linewidth=0.5, markersize=1)    ax[4].plot(range(0, len(stk_df['date'])), stk_df['MOM'], 'g*--', label='MOM', linewidth=0.5, markersize=1)    # 准备下标    xlabel_series = stk_df.apply(lambda x: x['date'][2:].replace('-', ''), axis=1)    ax[0] = addXticklabel(ax[0], xlabel_series, 40, rotation=45)    ax[1] = addXticklabel(ax[1], xlabel_series, 40, rotation=45)    ax[2] = addXticklabel(ax[2], xlabel_series, 40, rotation=45)    ax[3] = addXticklabel(ax[3], xlabel_series, 40, rotation=45)    ax[4] = addXticklabel(ax[4], xlabel_series, 40, rotation=45)    for ax_sig in ax:        ax_sig.legend(loc='best', fontsize=5)    fig.tight_layout()  # 调整整体空白    plt.subplots_adjust(wspace=0, hspace=0)  # 调整子图间距    ax[0].set_title(title_tmp)    return fig, ax, attention, result_analysisdef gen_hour_idx_pic(stk_df, stk_code='', debug=False):    """    打印常用指标    """    # 按升序排序    stk_df = stk_df.sort_values(by='datetime', ascending=True)    """    增加指标    'RSI5', 'RSI12', 'RSI30'    'SAR'    'slowk', 'slowd'    'upper', 'middle', 'lower'    'MOM'    """    stk_df = add_stk_index_to_df(stk_df).tail(60)    set_background_color(bc='w')    result_analysis = []    # 检查SAR    attention = False    sar_tail_origin = stk_df.tail(2)    sar_tail = sar_tail_origin.copy()    sar_tail['compare'] = sar_tail_origin.apply(lambda x: x['SAR'] - x['close'], axis=1)    title_tmp = stk_code + ' ' + code2name(stk_code)    if sar_tail.head(1)['compare'].values[0] * sar_tail.tail(1)['compare'].values[0] < 0:        if sar_tail.tail(1)['SAR'].values[0] < sar_tail.tail(1)['close'].values[0]:            title_tmp = stk_code + ' ' + code2name(stk_code) + ' 注意 SAR 指标翻转，后续数小时可能上涨！'            result_analysis.append(title_tmp)            set_background_color(bc='b_r')        else:            title_tmp = stk_code + ' ' + code2name(stk_code) + ' 注意 SAR 指标翻转，后续数小时可能下跌！'            result_analysis.append(title_tmp)            set_background_color(bc='b_g')        attention = True    fig, ax = plt.subplots(nrows=5, ncols=1)    ax[0].plot(range(0, len(stk_df['datetime'])), stk_df['RSI5'], 'b--', label='RSI5线', linewidth=1)    ax[0].plot(range(0, len(stk_df['datetime'])), stk_df['RSI12'], 'r--', label='RSI12线', linewidth=1)    ax[0].plot(range(0, len(stk_df['datetime'])), stk_df['RSI30'], 'g*--', label='RSI30', linewidth=0.5, markersize=1)    ax[0].plot(range(0, len(stk_df['datetime'])), [20 for a in range(len(stk_df['datetime']))], 'b--', linewidth=0.3)    ax[0].plot(range(0, len(stk_df['datetime'])), [80 for a in range(len(stk_df['datetime']))], 'b--', linewidth=0.3)    ax[0].set_ylim(0, 100)    ax[1].plot(range(0, len(stk_df['datetime'])), stk_df['SAR'], 'r--', label='SAR', linewidth=0.5, markersize=1)    ax[1].plot(range(0, len(stk_df['datetime'])), stk_df['close'], 'g*--', label='close', linewidth=0.5, markersize=1)    ax[2].plot(range(0, len(stk_df['datetime'])), stk_df['slowk'], 'g*--', label='slowk', linewidth=0.5, markersize=1)    ax[2].plot(range(0, len(stk_df['datetime'])), stk_df['slowd'], 'r*--', label='slowd', linewidth=0.5, markersize=1)    ax[2].plot(range(0, len(stk_df['datetime'])), [20 for a in range(len(stk_df['datetime']))], 'b--', linewidth=0.3)    ax[2].plot(range(0, len(stk_df['datetime'])), [80 for a in range(len(stk_df['datetime']))], 'b--', linewidth=0.3)    ax[2].set_ylim(0, 100)    ax[3].plot(range(0, len(stk_df['datetime'])), stk_df['upper'], 'r*--', label='布林上线', linewidth=0.5, markersize=1)    ax[3].plot(range(0, len(stk_df['datetime'])), stk_df['middle'], 'b*--', label='布林均线', linewidth=0.5, markersize=1)    ax[3].plot(range(0, len(stk_df['datetime'])), stk_df['lower'], 'g*--', label='布林下线', linewidth=0.5, markersize=1)    ax[4].plot(range(0, len(stk_df['datetime'])), stk_df['MOM'], 'g*--', label='MOM', linewidth=0.5, markersize=1)    # 准备下标    xlabel_series = stk_df.apply(lambda x: str(x['datetime'])[5:16], axis=1)    ax[4] = addXticklabel(ax[4], xlabel_series, 15, rotation=15, fontsize=7)    for ax_sig in ax:        ax_sig.legend(loc='best', fontsize=7)    fig.tight_layout()  # 调整整体空白    plt.subplots_adjust(wspace=0, hspace=0)  # 调整子图间距    ax[0].set_title(title_tmp)    # 打印过程日志    if debug:        txt_name = 'hour_index_pic'        # 打印时间        debug_print_txt(txt_name, stk_code, '时间：' + get_current_datetime_str() + '\n\n')        # 打印原始数据        debug_print_txt(txt_name, stk_code, stk_df.to_string() + '\n\n')        # 打印结果        debug_print_txt(txt_name, stk_code, '结果：\n' + str(result_analysis) + '\n\n')    return fig, ax, attention, result_analysisdef gen_idx_pic_wx(stk_df, stk_code=''):    r_tuple = gen_idx_pic(stk_df, stk_code=stk_code)    fig_tmp = r_tuple[0]    img = convert_fig_to_img(fig_tmp)    return img, r_tuple[3]def set_background_color(bc='w'):    """    设置背景色    :param bc:        b_r：背景红         #FA8072        b_g：背景绿         #98FB98        b_y：背景黄         #FFFFE0    :return:    """    if bc is 'b_r':        plt.rcParams['figure.facecolor'] = 'r'    elif bc is 'b_g':        plt.rcParams['figure.facecolor'] = 'g'    elif bc is 'b_y':        plt.rcParams['figure.facecolor'] = 'y'    else:        plt.rcParams['figure.facecolor'] = 'w'if __name__ == '__main__':    app = wx.App()    wx_img = wx.Image("C:\MoDeng\data/temp_pic/2019-11-24/000333\h150.png", wx.BITMAP_TYPE_PNG)    r = convert_pic_to_data("C:\MoDeng\data/temp_pic/2019-11-24/000333\h150.png")    from DataSource.auth_info import *    df_30 = get_k_data_JQ('002497', count=120,                          end_date=add_date_str(get_current_date_str(), 1), freq='30m')    fig = gen_hour_idx_pic(df_30, stk_code='')[0]    plt.show()    # r = gen_hour_macd_values('000001', source='jq', title='')    r = gen_hour_macd_pic('000001', source='jq', title='', debug=True)    plt.show()    df = get_k_data_JQ('002092', 400)    r = gen_idx_pic(df, stk_code='')    fig = gen_day_pic(df, stk_code='')[0]    plt.show()    end = 0